import styled from "styled-components";

export const CardsContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  background-color: #a3174a;
  opacity: 80%;
  padding: 2em;
  border-radius: 15px;
  box-shadow: 10px 10px 5px 0px rgba(158, 69, 69, 0.75);
  max-width: 90%;
  width: 100%;
  margin: auto;
`;
