const agregarPersonaje = (character, id) => {
    return {
        type: "ADD_CHARACTER",
        payload: {id}
    }
}

const eliminarPersonaje = (id) => {
    return {
        type: 'REMOVE_CHARACTER',
        payload: {id}
    }
}
