const ADD_FAVORITE = (character, id) => {
    return {
        type: "ADD_FAVORITE",
        payload: {id}
    }
}

const REMOVE_FAVORITE = (id) => {
    return {
        type: 'REMOVE_FAVORITE',
        payload: {id}
    }
}
