const addFavorite = (character, id) => {
    return {
        type: "ADD_FAVORITE",
        payload: {id}
    }
}

const removeFavorite = (id) => {
    return {
        type: 'REMOVE_FAVORITE',
        payload: {id}
    }
}

export default (addFavorite)(removeFavorite);
