import React from "react";
import style from "./Card.module.css"
import { Link } from "react-router-dom";
import {agregarPersonaje, eliminarPersonaje} from "../../redux/actions.js"
import { Connect } from "react-redux";

const Card = ({ id, name, species, gender, image, onClose }) => {
   const mapDispatchToProps = (dispatch) => {
      return {
         agregarPersonaje: () => {
            dispatch(agregarPersonaje());
         },
         eliminarPersonaje: () => {
            dispatch(eliminarPersonaje());
         }
      }
   }
   
   
   return (
      <div className={style.container}>
         <button
            onClick={() => onClose(id)}
            className={style.closeButton}
         >
            X
         </button>
         <img src={image} alt="" />
         <Link to={`/detail/${id}`}>
            <h2 className={style.h2}>{name}</h2>
         </Link>
         <h2 className={style.h2}>Species: {species}</h2>
         <h2 className={style.h2}>Gender: {gender}</h2>
      </div>
   );
}

export default connect(null, mapDispatchToProps)(Card);
